import React from 'react'

class Badgeform extends React.Component {

    state={
        
    }
    handleChange =(e) => {
        //console.log({
        //    value:e.target.value, 
        //    name:e.target.name
        //});

        this.setState({
            [e.target.name]:e.target.value,
        })
    };

    handleClick=(e)=>{
        console.log('button was clicked');
    };

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log("se queda en el from")
    }

    render() {
        return (
            <div className="">
            <h1>nuevo formulario</h1>
                
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label >Nombre</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="nombre" 
                        value={this.state.nombre}
                        />
                    </div>

                    <div className="form-group">
                        <label >Apellido</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="apellido"
                            value={this.state.apellido}
                        />
                    </div>
                    
                    <div className="form-group">
                        <label >Email</label>
                        <input onChange={this.handleChange} className="form-control" type="email" name="email"
                            value={this.state.email}
                        />
                    </div>
                    <div className="form-group">
                        <label >Trabajo</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="trabajo"
                            value={this.state.trabajo}
                        />
                    </div>

                    <div className="form-group">
                        <label >Twitter</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="titter"
                            value={this.state.titter}
                        />
                    </div>

                    <button type="submit" onClick={this.handleClick} className="btn btn-success">guardar</button>
                </form>
                
                
            </div>
        )
    }
}

export default Badgeform
