import React from 'react'

class Badgeform extends React.Component {

    handleChange =(e) => {
        //console.log({
        //    value:e.target.value, 
        //    name:e.target.name
        //});

        this.setState({
            nombre:e.target.value,
        })
    };

    handleClick=(e)=>{
        console.log('button was clicked');
    };

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log("se queda en el from")
    }

    render() {
        return (
            <div className="">
            <h1>nuevo formulario</h1>
                
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label >Nombre</label>
                        <input onChange={this.handleChange} className="form-control" type="text" name="nombre"/>
                    </div>
                    <button type="submit" onClick={this.handleClick} className="btn btn-success">guardar</button>
                </form>
                
            </div>
        )
    }
}

export default Badgeform
